import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { userService } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { PolicyPage } from './07_PolicyPage';

const countryCodes = [
  { code: '+970', flag: 'üáµüá∏', name: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ' }, { code: '+962', flag: 'üáØüá¥', name: 'ÿßŸÑÿ£ÿ±ÿØŸÜ' },
  { code: '+966', flag: 'üá∏üá¶', name: 'ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©' }, { code: '+20', flag: 'üá™üá¨', name: 'ŸÖÿµÿ±' },
  { code: '+971', flag: 'üá¶üá™', name: 'ÿßŸÑÿ•ŸÖÿßÿ±ÿßÿ™' }, { code: '+90', flag: 'üáπüá∑', name: 'ÿ™ÿ±ŸÉŸäÿß' },
];

const translations = {
  ar: {
    login: "ÿØÿÆŸàŸÑ", signup: "ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ", email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä", phone: "ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ",
    password: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", confirmPassword: "ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±",
    firstName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ", lastName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ÿÆŸäÿ±", companyName: "ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ© / ÿßŸÑÿ¥ÿ±ŸÉÿ©",
    country: "ÿßÿÆÿ™ÿ± ÿßŸÑÿ®ŸÑÿØ", mustAgree: "Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ÿπŸÑŸâ ÿßŸÑÿ≥Ÿäÿßÿ≥ÿ©", loading: "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...",
    aiAnalyzing: "ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ©... ü§ñ", invalidFace: "‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ÿµŸàÿ±ÿ© Ÿàÿ¨Ÿá", invalidLogo: "‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÑŸàÿ¨Ÿà"
  },
  en: {
    login: "Login", signup: "Sign Up", email: "Email", phone: "Phone",
    password: "Password", confirmPassword: "Confirm Password",
    firstName: "First Name", lastName: "Last Name", companyName: "Company Name"
  }
};

export const AuthPage = ({ lang = 'ar' }) => {
  const { login: performContextLogin } = useAuth();
  const t = translations[lang] || translations.ar;
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [userType, setUserType] = useState('individuals');
  const [loginMethod, setLoginMethod] = useState('phone'); 
  const [showPass, setShowPass] = useState(false);
  const [agreed, setAgreed] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  const [showPolicy, setShowPolicy] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [imgAnalyzing, setImgAnalyzing] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const [formData, setFormData] = useState({
    firstName: '', lastName: '', companyName: '', email: '', password: '', 
    confirmPassword: '', phone: '', country: ''
  });

  useEffect(() => { setIsVisible(true); }, []);

  const handleInputChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const getPhoto = async (source) => {
    setShowPhotoModal(false);
    setTimeout(async () => {
      try {
        const image = await Camera.getPhoto({ quality: 90, allowEditing: true, resultType: CameraResultType.Base64, source });
        const base64Img = `data:image/jpeg;base64,${image.base64String}`;
        setImgAnalyzing(true);
        try {
          const res = await userService.analyzeImage({ image: base64Img, targetType: userType === 'companies' ? 'logo' : 'face' });
          if (res.data.isValid) setProfileImage(base64Img);
          else { setError(userType === 'companies' ? t.invalidLogo : t.invalidFace); setProfileImage(null); }
        } catch (e) { setProfileImage(base64Img); } 
        finally { setImgAnalyzing(false); }
      } catch (e) {}
    }, 300);
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    if (!isLogin && !agreed) { setError(t.mustAgree); return; }
    setError('');
    setLoading(true);
    try {
      const payload = isLogin 
        ? (loginMethod === 'email' ? { email: formData.email, password: formData.password } : { phone: formData.phone, password: formData.password })
        : { ...formData, profileImage, role: userType === 'companies' ? 'HR' : 'Employee' };
      const res = isLogin ? await userService.login(payload) : await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);
      navigate(res.data.user.role === 'HR' ? '/onboarding-companies' : '/onboarding-individuals');
    } catch (err) { setError(err.userMessage || "Error"); } 
    finally { setLoading(false); }
  };

  return (
    <div className={`min-h-screen w-full flex flex-col items-center justify-center p-4 bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={lang === 'ar' ? 'rtl' : 'ltr'}>
      {showPolicy && <div className="fixed inset-0 z-[12000]"><PolicyPage lang={lang} onAccept={() => { setAgreed(true); setShowPolicy(false); }} /></div>}

      <div className={`w-full max-w-lg bg-white rounded-[4rem] shadow-2xl p-8 md:p-14 border border-white transform transition-all duration-1000 ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        <div className="text-center mb-8">
          <img src="/logo.jpg" alt="Logo" className="h-20 w-20 mx-auto rounded-full shadow-xl mb-4 border-4 border-[#E3DAD0]" />
          <h2 className="text-3xl font-black text-[#1A365D]">{isLogin ? t.login : t.signup}</h2>
          {imgAnalyzing && <p className="text-[10px] text-blue-600 animate-pulse mt-2">{t.aiAnalyzing}</p>}
        </div>

        {/* ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ÿ®ŸäŸÜ ÿßŸÑÿ≠ÿßŸÑÿßÿ™ */}
        <div className="flex bg-gray-100 p-2 rounded-3xl mb-8">
          <button onClick={() => setIsLogin(true)} className={`flex-1 py-4 rounded-2xl font-black ${isLogin ? 'bg-white text-[#1A365D] shadow-sm' : 'text-gray-400'}`}>{t.login}</button>
          <button onClick={() => setIsLogin(false)} className={`flex-1 py-4 rounded-2xl font-black ${!isLogin ? 'bg-white text-[#1A365D] shadow-sm' : 'text-gray-400'}`}>{t.signup}</button>
        </div>

        {/* ÿßŸÑÿ™ÿ®ÿØŸäŸÑ ÿ®ŸäŸÜ ÿ£ŸÅÿ±ÿßÿØ Ÿàÿ¥ÿ±ŸÉÿßÿ™ (ŸÅŸÇÿ∑ ŸÅŸä ÿ≠ÿßŸÑ ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ¨ÿØŸäÿØ) */}
        {!isLogin && (
          <div className="flex gap-2 p-1 bg-[#1A365D]/5 rounded-2xl mb-8">
            <button onClick={() => setUserType('individuals')} className={`flex-1 py-3 rounded-xl text-[11px] font-black ${userType === 'individuals' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-gray-400'}`}>{t.ar.individuals}</button>
            <button onClick={() => setUserType('companies')} className={`flex-1 py-3 rounded-xl text-[11px] font-black ${userType === 'companies' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-gray-400'}`}>{t.ar.companies}</button>
          </div>
        )}

        <form onSubmit={handleAuth} className="space-y-5">
          {!isLogin && (
            <div className="flex flex-col items-center mb-6">
              <button type="button" onClick={() => setShowPhotoModal(true)} className="w-24 h-24 rounded-full bg-gray-50 border-4 border-white shadow-xl flex items-center justify-center overflow-hidden">
                {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <span className="text-4xl opacity-20">üë§</span>}
              </button>
            </div>
          )}

          <div className="space-y-4">
            {/* ÿ≠ŸÇŸàŸÑ ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ¨ÿØŸäÿØ ŸÅŸÇÿ∑ */}
            {!isLogin && (
              <>
                <select name="country" onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-[2.5rem] outline-none font-bold text-sm text-center">
                  <option value="">-- {t.country} --</option>
                  {countryCodes.map(c => <option key={c.code} value={c.name}>{c.flag} {c.name}</option>)}
                </select>
                {userType === 'individuals' ? (
                  <div className="grid grid-cols-2 gap-3">
                    <input type="text" name="firstName" placeholder={t.firstName} onChange={handleInputChange} className="p-5 bg-gray-50 rounded-3xl font-bold text-center text-sm" required />
                    <input type="text" name="lastName" placeholder={t.lastName} onChange={handleInputChange} className="p-5 bg-gray-50 rounded-3xl font-bold text-center text-sm" required />
                  </div>
                ) : (
                  <input type="text" name="companyName" placeholder={t.companyName} onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-3xl font-bold text-center text-sm" required />
                )}
              </>
            )}

            {/* ÿ≠ŸÇŸàŸÑ ŸÖÿ¥ÿ™ÿ±ŸÉÿ© (ÿØÿÆŸàŸÑ Ÿàÿßÿ¥ÿ™ÿ±ÿßŸÉ) */}
            <div className="flex gap-2 justify-center mb-2">
              <button type="button" onClick={() => setLoginMethod('phone')} className={`px-6 py-2 rounded-full text-[10px] font-black ${loginMethod === 'phone' ? 'bg-[#1A365D] text-white shadow-md' : 'bg-gray-100 text-gray-400'}`}>ÿ®ÿßŸÑÿ¨ŸàÿßŸÑ</button>
              <button type="button" onClick={() => setLoginMethod('email')} className={`px-6 py-2 rounded-full text-[10px] font-black ${loginMethod === 'email' ? 'bg-[#1A365D] text-white shadow-md' : 'bg-gray-100 text-gray-400'}`}>ÿ®ÿßŸÑÿ®ÿ±ŸäÿØ</button>
            </div>

            {loginMethod === 'phone' ? (
              <div className="flex items-center bg-gray-50 rounded-3xl p-1">
                <select className="bg-transparent font-black text-xs px-3">
                  {countryCodes.map(c => <option key={c.code} value={c.code}>{c.code}</option>)}
                </select>
                <input type="tel" name="phone" onChange={handleInputChange} placeholder={t.phone} className="flex-1 p-4 bg-transparent outline-none font-black text-center text-lg" required />
              </div>
            ) : (
              <input type="email" name="email" onChange={handleInputChange} placeholder={t.email} className="w-full p-5 bg-gray-50 rounded-[2.5rem] outline-none font-bold text-sm text-center" required />
            )}

            <input type="password" name="password" onChange={handleInputChange} placeholder={t.password} className="w-full p-5 bg-gray-50 rounded-[2.5rem] outline-none font-bold text-sm text-center" required />
            {!isLogin && <input type="password" name="confirmPassword" onChange={handleInputChange} placeholder={t.confirmPassword} className="w-full p-5 bg-gray-50 rounded-[2.5rem] outline-none font-bold text-sm text-center" required />}
          </div>

          {!isLogin && (
            <div className="flex items-center gap-2 text-[10px] font-bold text-gray-400 px-4">
              <input type="checkbox" checked={agreed} onChange={(e) => setAgreed(e.target.checked)} className="w-4 h-4" />
              <p>ÿ£ŸàÿßŸÅŸÇ ÿπŸÑŸâ <button type="button" onClick={() => setShowPolicy(true)} className="text-[#1A365D] underline">ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©</button></p>
            </div>
          )}

          {error && <div className="p-4 bg-red-50 text-red-600 rounded-2xl text-[11px] font-black text-center animate-shake">{error}</div>}
          <button type="submit" disabled={loading} className="w-full py-6 rounded-[3rem] bg-[#1A365D] text-white font-black shadow-2xl active:scale-95 transition-all text-xl">
            {loading ? '...' : (isLogin ? t.login : t.signup)}
          </button>
        </form>
      </div>

      {showPhotoModal && (
        <div className="fixed inset-0 z-[11000] flex items-end justify-center bg-black/40" onClick={() => setShowPhotoModal(false)}>
          <div className="bg-white w-full max-w-lg rounded-t-[3rem] p-10 shadow-2xl" onClick={e => e.stopPropagation()}>
            <div className={`grid ${userType === 'companies' ? 'grid-cols-1' : 'grid-cols-2'} gap-4`}>
              {userType !== 'companies' && <button onClick={() => getPhoto(CameraSource.Camera)} className="p-8 bg-gray-50 rounded-3xl font-black text-[#1A365D]">üì∏ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß</button>}
              <button onClick={() => getPhoto(CameraSource.Photos)} className="p-8 bg-gray-50 rounded-3xl font-black text-[#1A365D]">üñºÔ∏è ÿßŸÑŸÖÿπÿ±ÿ∂</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
