import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslate } from '../hooks/useTranslate';

export const WelcomePage = () => {
  const navigate = useNavigate();
  const t = useTranslate();

  return (
    <div className="w-full min-h-[80vh] flex items-center justify-center">
      <div className="bg-white w-full max-w-md rounded-[3rem] shadow-2xl p-10 text-center">
        <h1 className="text-3xl font-black text-[#1A365D]">
          {t.welcome.title}
        </h1>

        <p className="text-gray-500 font-semibold mb-8">
          {t.welcome.subtitle}
        </p>

        <button
          onClick={() => navigate('/onboarding-individuals')}
          className="w-full py-4 rounded-full bg-[#1A365D] text-white font-bold"
        >
          {t.welcome.individual}
        </button>
      </div>
    </div>
  );
};
