import React from 'react';
import { useNavigate } from 'react-router-dom';

export const PolicyPage = ({ lang, onAccept }) => {
  const navigate = useNavigate();
  
  const content = {
    ar: {
      title: 'سياسة الخصوصية - Careerak',
      intro: 'مرحبًا بك في تطبيق Careerak. نحن نحترم خصوصيتك ونلتزم بحماية بياناتك الشخصية. توافقك على هذه السياسة يضمن لك تجربة مهنية آمنة.',
      sections: [
        { h: '1. المعلومات التي نجمعها', p: 'نجمع الاسم، البريد، رقم الهاتف، والبيانات المهنية لتوفير أفضل فرص العمل والتدريب.' },
        { h: '2. استخدام البيانات', p: 'تُستخدم البيانات لإدارة حسابك وتخصيص المقترحات الذكية وتحسين أداء النظام.' },
        { h: '3. حماية المعلومات', p: 'نستخدم أحدث تقنيات التشفير لضمان أمان بياناتك الشخصية والمهنية.' }
      ],
      back: 'فهمت وأوافق'
    },
    en: {
      title: 'Privacy Policy - Careerak',
      intro: 'Welcome to Careerak. We respect your privacy and protect your personal data.',
      sections: [
        { h: '1. Data Collection', p: 'We collect name, email, phone, and professional data to provide best job opportunities.' },
        { h: '2. Usage', p: 'Data is used for account management and AI recommendations.' },
        { h: '3. Security', p: 'We use advanced encryption to secure your information.' }
      ],
      back: 'I Accept'
    },
    fr: {
      title: 'Politique de Confidentialité',
      intro: 'Bienvenue sur Careerak. Nous respectons votre vie privée.',
      sections: [
        { h: '1. Collecte de données', p: 'Nous collectons votre nom, e-mail et données professionnelles.' },
        { h: '2. Utilisation', p: 'Les données sont utilisées pour la gestion du compte.' },
        { h: '3. Sécurité', p: 'Nous utilisons un cryptage avancé.' }
      ],
      back: 'J\'accepte'
    }
  }[lang] || {};

  const handleBack = () => {
    if (onAccept) onAccept(); // إذا تم استدعاؤه كـ Overlay
    else navigate(-1); // إذا تم الدخول إليه كصفحة مستقلة
  };

  return (
    <div className="min-h-screen p-4 py-8 bg-[#E3DAD0]/95 backdrop-blur-md overflow-y-auto" dir={lang === 'ar' ? 'rtl' : 'ltr'}>
      <div className="max-w-2xl mx-auto bg-white rounded-[3rem] shadow-2xl p-8 md:p-12 border border-white">
        <h1 className="text-3xl font-black text-[#1A365D] mb-6 border-b-4 border-blue-50 pb-2">{content.title}</h1>
        <p className="text-gray-600 font-bold mb-8 leading-relaxed">{content.intro}</p>
        
        <div className="space-y-6">
          {content.sections && content.sections.map((s, i) => (
            <div key={i} className="bg-gray-50 p-5 rounded-3xl">
              <h2 className="text-lg font-black text-[#1A365D] mb-2">{s.h}</h2>
              <p className="text-gray-500 font-bold text-sm leading-relaxed">{s.p}</p>
            </div>
          ))}
        </div>

        <button 
          onClick={handleBack}
          className="w-full mt-10 bg-[#1A365D] text-white py-5 rounded-[2rem] font-black shadow-xl active:scale-95 transition-all text-xl"
        >
          {content.back}
        </button>
      </div>
    </div>
  );
};
