import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext";
import { AppSettingsProvider } from "./context/AppSettingsContext";

// استيراد الصفحات المعتمدة فقط
import LanguagePage from "./pages/00_LanguagePage";
import EntryPage from "./pages/01_EntryPage";
import LoginPage from "./pages/03_LoginPage";
import AuthPage from "./pages/04_AuthPage";

function AppRoutes() {
  const { language, audioEnabled, loading } = useAuth();

  if (loading) return null;

  // الحماية: إذا لم يتم اختيار اللغة، ابقَ في صفحة اللغة
  if (!language || audioEnabled === null) {
    return (
      <Routes>
        <Route path="*" element={<LanguagePage />} />
      </Routes>
    );
  }

  return (
    <Routes>
      <Route path="/" element={<LanguagePage />} />
      <Route path="/entry" element={<EntryPage />} />
      <Route path="/login" element={<LoginPage />} />
      <Route path="/auth" element={<AuthPage lang={language} />} />
      
      {/* المسار الافتراضي عند ضياع المستخدم */}
      <Route path="*" element={<EntryPage />} />
    </Routes>
  );
}

export default function App() {
  return (
    <AppSettingsProvider>
      <AuthProvider>
        <Router>
          <AppRoutes />
        </Router>
      </AuthProvider>
    </AppSettingsProvider>
  );
}
