import React from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const translations = {
  ar: {
    title: "ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™",
    langSection: "ŸÑÿ∫ÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ",
    desc: "ÿßÿÆÿ™ÿ± ŸÑÿ∫ÿ™ŸÉ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ŸÑÿ™ÿ∫ŸäŸäÿ± Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ®ÿßŸÑŸÉÿßŸÖŸÑ",
    logout: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨",
    back: "ÿ±ÿ¨Ÿàÿπ"
  },
  en: {
    title: "Settings",
    langSection: "App Language",
    desc: "Choose your preferred language to change the entire app interface",
    logout: "Logout",
    back: "Back"
  },
  fr: {
    title: "Param√®tres",
    langSection: "Langue de l'application",
    desc: "Choisissez votre langue pr√©f√©r√©e pour changer toute l'interface",
    logout: "Se d√©connecter",
    back: "Retour"
  }
};

export const SettingsPage = ({ lang }) => {
  const { changeLanguage, logout, language } = useAuth();
  const navigate = useNavigate();
  const t = translations[language] || translations.en;

  const languages = [
    { id: 'ar', label: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' },
    { id: 'en', label: 'English', flag: 'üá∫üá∏' },
    { id: 'fr', label: 'Fran√ßais', flag: 'üá´üá∑' }
  ];

  return (
    <div className="min-h-screen pb-20 animate-fade-in">
      <div className="flex items-center justify-between mb-8">
        <h1 className="text-3xl font-black text-[#1A365D]">{t.title}</h1>
        <button onClick={() => navigate(-1)} className="p-3 bg-white rounded-2xl shadow-sm text-sm font-bold">{t.back}</button>
      </div>

      <div className="bg-white rounded-[2.5rem] p-8 shadow-xl border border-white mb-6">
        <div className="flex items-center gap-4 mb-6">
          <div className="w-12 h-12 bg-blue-50 rounded-2xl flex items-center justify-center text-2xl">üåê</div>
          <div>
            <h2 className="font-black text-[#1A365D]">{t.langSection}</h2>
            <p className="text-xs text-gray-400 font-bold">{t.desc}</p>
          </div>
        </div>

        <div className="space-y-3">
          {languages.map((l) => (
            <button
              key={l.id}
              onClick={() => changeLanguage(l.id)}
              className={`w-full p-5 rounded-3xl flex items-center justify-between transition-all ${
                language === l.id 
                ? 'bg-[#1A365D] text-white shadow-lg scale-[1.02]' 
                : 'bg-gray-50 text-gray-600 hover:bg-gray-100'
              }`}
            >
              <div className="flex items-center gap-4">
                <span className="text-2xl">{l.flag}</span>
                <span className="font-black">{l.label}</span>
              </div>
              {language === l.id && <span className="text-xl">‚úì</span>}
            </button>
          ))}
        </div>
      </div>

      <button 
        onClick={logout}
        className="w-full p-6 bg-red-50 text-red-600 rounded-[2rem] font-black flex items-center justify-center gap-3 active:scale-95 transition-all"
      >
        <span>üö™</span>
        {t.logout}
      </button>
    </div>
  );
};
