import React from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import logo from "../assets/logo.png";

export const LanguagePage = () => {
  const navigate = useNavigate();
  const { setLanguage } = useAuth();

  const selectLanguage = (lang) => {
    setLanguage(lang);
    setTimeout(() => {
      navigate("/country");
    }, 100);
  };

  return (
    <div className="relative min-h-screen bg-secondary overflow-hidden flex flex-col items-center justify-center">

      {/* CSS محلي للشفافية */}
      <style>{`
        @keyframes navyPulse {
          0% {
            transform: translate(-50%, -50%) scale(0.3);
            opacity: 0;
          }
          25% {
            opacity: 0.35;
          }
          50% {
            transform: translate(-50%, -50%) scale(2.5);
            opacity: 0.25;
          }
          75% {
            opacity: 0;
          }
          100% {
            transform: translate(-50%, -50%) scale(0.3);
            opacity: 0;
          }
        }

        .navy-pulse {
          position: absolute;
          top: 50%;
          left: 50%;
          width: 120vmax;
          height: 120vmax;
          background-color: #1A365D;
          border-radius: 50%;
          animation: navyPulse 10s ease-in-out infinite;
          z-index: 0;
        }
      `}</style>

      {/* الشفافية */}
      <div className="navy-pulse" />

      {/* المحتوى */}
      <div className="relative z-10 w-full flex flex-col items-center text-center px-6">

        {/* اللوجو */}
        <div className="mb-10 w-40 h-40 rounded-full border-2 border-primary shadow-2xl flex items-center justify-center bg-secondary">
          <img
            src={logo}
            alt="Careerak Logo"
            className="w-28 h-28 object-contain"
          />
        </div>

        {/* العناوين */}
        <h1 className="text-primary text-xl font-bold mb-1">
          اختر اللغة / Choose Language
        </h1>
        <h1 className="text-primary text-xl font-bold mb-8">
          Choisir la langue
        </h1>

        {/* الأزرار */}
        <div className="w-full max-w-sm space-y-4">
          <button
            onClick={() => selectLanguage("ar")}
            className="w-full py-4 rounded-full bg-primary text-white font-bold text-lg active:scale-95 transition"
          >
            العربية
          </button>

          <button
            onClick={() => selectLanguage("en")}
            className="w-full py-4 rounded-full bg-primary text-white font-bold text-lg active:scale-95 transition"
          >
            English
          </button>

          <button
            onClick={() => selectLanguage("fr")}
            className="w-full py-4 rounded-full bg-primary text-white font-bold text-lg active:scale-95 transition"
          >
            Français
          </button>
        </div>
      </div>
    </div>
  );
};
