import React, { createContext, useContext, useState, useEffect } from 'react';
import { Preferences } from '@capacitor/preferences';
import CryptoJS from 'crypto-js';

const AuthContext = createContext();
const SECRET_KEY = 'careerak_secure_key_2024';

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [language, setLanguage] = useState(null);

  const encrypt = (text) => CryptoJS.AES.encrypt(text, SECRET_KEY).toString();
  const decrypt = (cipherText) => {
    try {
      const bytes = CryptoJS.AES.decrypt(cipherText, SECRET_KEY);
      return bytes.toString(CryptoJS.enc.Utf8);
    } catch (e) { return null; }
  };

  useEffect(() => {
    const loadData = async () => {
      try {
        // ðŸ›¡ï¸ Ø±ØºØ¯: Ù‚Ø±Ø§Ø¡Ø© Ø§Ù„Ù„ØºØ© Ù…Ù† Preferences Ø§Ù„Ø£Ù†Ø¯Ø±ÙˆÙŠØ¯ Ù„Ø¶Ù…Ø§Ù† Ø§Ù„Ø¯Ù‚Ø©
        const { value: savedLang } = await Preferences.get({ key: 'lang' });
        if (savedLang) {
          setLanguage(savedLang);
          document.documentElement.dir = savedLang === 'ar' ? 'rtl' : 'ltr';
          document.documentElement.lang = savedLang;
        }

        const { value: encryptedToken } = await Preferences.get({ key: 'auth_token' });
        const storedUser = localStorage.getItem('user');

        if (encryptedToken && storedUser) {
          const token = decrypt(encryptedToken);
          if (token) {
            setUser(JSON.parse(storedUser));
            setIsAuthenticated(true);
          }
        }
      } catch (error) {
        console.error('Auth Init Error:', error);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  const login = async (userData, token) => {
    const encryptedToken = encrypt(token);
    await Preferences.set({ key: 'auth_token', value: encryptedToken });
    localStorage.setItem('user', JSON.stringify(userData));
    setUser(userData);
    setIsAuthenticated(true);
  };

  const logout = async () => {
    await Preferences.remove({ key: 'auth_token' });
    await Preferences.remove({ key: 'lang' }); // Ù…Ø³Ø­ Ø§Ù„Ù„ØºØ© Ø¹Ù†Ø¯ Ø§Ù„Ø®Ø±ÙˆØ¬ Ù„Ø¶Ù…Ø§Ù† Ø§Ù„Ø¸Ù‡ÙˆØ± Ù…Ø¬Ø¯Ø¯Ø§Ù‹
    localStorage.clear();
    setUser(null);
    setIsAuthenticated(false);
    window.location.href = '/';
  };

  const changeLanguage = async (lang) => {
    setLanguage(lang);
    await Preferences.set({ key: 'lang', value: lang });
    document.documentElement.dir = lang === 'ar' ? 'rtl' : 'ltr';
    document.documentElement.lang = lang;
  };

  return (
    <AuthContext.Provider value={{ 
      user, isAuthenticated, loading, login, logout, 
      language, changeLanguage 
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
