import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export const OnboardingIndividuals = ({ lang }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [parsing, setParsing] = useState(false);
  const [error, setError] = useState('');
  const [isAgreed, setIsAgree] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [fileName, setFileName] = useState('');
  
  const [formData, setFormData] = useState({
    bio: '',
    educationList: [{ degree: '', university: '', year: '' }],
    experienceList: [{ title: '', company: '', city: '', durationYears: '', durationMonths: '', tasks: '' }],
    skills: '',
    cvFile: null
  });

  const t = {
    ar: { 
      title: 'ÿ®ŸÜÿßÿ° ŸÖŸÑŸÅŸÉ ÿßŸÑÿßÿ≠ÿ™ÿ±ÿßŸÅŸä ‚ú®', 
      sub: 'ÿßÿ±ŸÅÿπ ÿ≥Ÿäÿ±ÿ™ŸÉ ÿßŸÑÿ∞ÿßÿ™Ÿäÿ© Ÿàÿ≥ŸäŸÇŸàŸÖ ÿ∞ŸÉÿßÿ° ŸÉÿßÿ±Ÿäÿ±ŸÉ ÿ®ÿ™ÿπÿ®ÿ¶ÿ© ÿ®ŸäÿßŸÜÿßÿ™ŸÉ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã!',
      upload: 'ÿßÿ±ŸÅÿπ ÿßŸÑÿ≥Ÿäÿ±ÿ© ÿßŸÑÿ∞ÿßÿ™Ÿäÿ©',
      parsing: 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ∞ŸÉŸä... ü§ñ',
      bio: 'ŸÜÿ®ÿ∞ÿ© ÿ™ÿπÿ±ŸäŸÅŸäÿ© ŸÇÿµŸäÿ±ÿ©',
      eduTitle: 'ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿØÿ±ÿßÿ≥Ÿä',
      expTitle: 'ÿßŸÑÿÆÿ®ÿ±ÿßÿ™ ÿßŸÑÿπŸÖŸÑŸäÿ©',
      addEdu: '+ ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿ§ŸáŸÑ ÿØÿ±ÿßÿ≥Ÿä',
      addExp: '+ ÿ•ÿ∂ÿßŸÅÿ© ÿÆÿ®ÿ±ÿ© ÿπŸÖŸÑŸäÿ©',
      degree: 'ÿßŸÑŸÖÿ§ŸáŸÑ ÿßŸÑÿπŸÑŸÖŸä',
      uni: 'ÿßŸÑÿ¨ÿßŸÖÿπÿ© / ÿßŸÑÿ¨Ÿáÿ©',
      year: 'ÿ≥ŸÜÿ© ÿßŸÑÿ™ÿÆÿ±ÿ¨',
      jobTitle: 'ÿßŸÑŸÖÿ≥ŸÖŸâ ÿßŸÑŸàÿ∏ŸäŸÅŸä',
      company: 'ÿßŸÑÿ¥ÿ±ŸÉÿ© / ÿßŸÑÿ¨Ÿáÿ©',
      city: 'ÿßŸÑŸÖÿØŸäŸÜÿ©',
      years: 'ÿ≥ŸÜŸäŸÜ',
      months: 'ÿ£ÿ¥Ÿáÿ±',
      tasks: 'ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑŸàÿ∏ŸäŸÅŸäÿ© ŸàÿßŸÑÿ•ŸÜÿ¨ÿßÿ≤ÿßÿ™',
      skills: 'ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©',
      declaration: 'ÿ£ŸÇÿ± ÿ®ÿ£ŸÜ ŸÉÿßŸÅÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿµÿ≠Ÿäÿ≠ÿ© Ÿàÿ™ÿ≠ÿ™ ŸÖÿ≥ÿ§ŸàŸÑŸäÿ™Ÿä ÿßŸÑÿ¥ÿÆÿµŸäÿ©.',
      finish: 'ÿ•ÿ™ŸÖÿßŸÖ ÿßŸÑŸÖŸÑŸÅ ŸàÿßŸÑÿ®ÿØÿ° ÿ®ÿ±ÿ≠ŸÑÿ© ÿßŸÑŸÜÿ¨ÿßÿ≠',
      sizeError: 'ÿßŸÑŸÖŸÑŸÅ ŸÉÿ®Ÿäÿ± ÿ¨ÿØÿßŸã (ÿßŸÑÿ£ŸÇÿµŸâ 10MB)',
      modalMsg: 'Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ÿπŸÑŸâ ÿµÿ≠ÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ£ŸàŸÑÿßŸã',
      close: 'ÿ≠ÿ≥ŸÜÿßŸã'
    },
    en: {
        title: 'Build Your Professional Profile ‚ú®',
        sub: 'Upload CV and let AI fill your data!',
        upload: 'Upload CV',
        parsing: 'AI is parsing... ü§ñ',
        bio: 'Short Bio',
        eduTitle: 'Education',
        expTitle: 'Work Experience',
        addEdu: '+ Add Education',
        addExp: '+ Add Experience',
        degree: 'Degree',
        uni: 'University',
        year: 'Graduation Year',
        jobTitle: 'Job Title',
        company: 'Company',
        city: 'City',
        years: 'Years',
        months: 'Months',
        tasks: 'Tasks & Achievements',
        skills: 'Key Skills',
        declaration: 'I declare that all data is correct.',
        finish: 'Finish and Start',
        sizeError: 'File too large (Max 10MB)',
        modalMsg: 'Please agree to declaration',
        close: 'OK'
    }
  }[lang || 'ar'];

  const handleAddEdu = () => setFormData({...formData, educationList: [...formData.educationList, { degree: '', university: '', year: '' }]});
  const handleAddExp = () => setFormData({...formData, experienceList: [...formData.experienceList, { title: '', company: '', city: '', durationYears: '', durationMonths: '', tasks: '' }]});

  const handleCVUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (file.size > 10 * 1024 * 1024) { setError(t.sizeError); return; }

    setFileName(file.name);
    setParsing(true);
    setError('');

    const reader = new FileReader();
    reader.onloadend = async () => {
      try {
        const base64 = reader.result;
        const res = await api.post('/users/parse-cv', { cvBase64: base64, fileName: file.name });
        const ai = res.data.data;
        // ÿØŸÖÿ¨ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿ±ÿ¨ÿ© ÿ®ÿ∞ŸÉÿßÿ°
        setFormData({
          ...formData,
          bio: ai.bio || formData.bio,
          educationList: ai.education || formData.educationList,
          experienceList: ai.experience || formData.experienceList,
          skills: ai.skills || formData.skills,
          cvFile: base64
        });
      } catch (err) {
        setFormData({ ...formData, cvFile: reader.result });
      } finally { setParsing(false); }
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isAgreed) { setShowModal(true); return; }
    setLoading(true);
    try {
      const res = await api.put('/users/profile', formData);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/dashboard');
    } catch (err) { setError(err.userMessage || t.error); } 
    finally { setLoading(false); }
  };

  return (
    <div className="min-h-screen p-4 md:p-8 bg-[#E3DAD0] pb-20">
      {showModal && (
        <div className="fixed inset-0 z-[11000] flex items-center justify-center p-6 bg-black/40 backdrop-blur-sm">
          <div className="bg-white rounded-[2.5rem] p-8 max-w-sm w-full text-center shadow-2xl border-b-8 border-[#1A365D]">
            <p className="text-[#1A365D] font-black text-lg mb-6">{t.modalMsg}</p>
            <button onClick={() => setShowModal(false)} className="w-full py-4 bg-[#1A365D] text-white rounded-2xl font-black">ÿ≠ÿ≥ŸÜÿßŸã</button>
          </div>
        </div>
      )}

      <div className="max-w-3xl mx-auto bg-white rounded-[3.5rem] shadow-2xl p-6 md:p-12 border border-white">
        <div className="text-center mb-10">
          <h2 className="text-3xl font-black text-[#1A365D] mb-2">{t.title}</h2>
          <p className="text-gray-400 font-bold text-sm">{t.sub}</p>
        </div>

        <label className={`mb-10 p-8 border-4 border-dashed rounded-[3rem] transition-all cursor-pointer block text-center ${fileName ? 'bg-green-50 border-green-200' : 'bg-blue-50/30 border-blue-50 hover:bg-white'}`}>
          {parsing ? <p className="animate-pulse text-blue-600 font-black">{t.parsing}</p> : 
            <><div className="text-3xl mb-2">{fileName ? '‚úÖ' : 'üìÑ'}</div><p className="font-black text-[#1A365D]">{fileName || t.upload}</p></>
          }
          <input type="file" className="hidden" onChange={handleCVUpload} />
        </label>

        <form onSubmit={handleSubmit} className="space-y-10 text-right" dir="rtl">
          {/* ÿßŸÑŸÜÿ®ÿ∞ÿ© */}
          <section className="space-y-3">
            <h3 className="text-lg font-black text-[#1A365D] border-r-4 border-[#1A365D] pr-3">{t.bio}</h3>
            <textarea value={formData.bio} onChange={e => setFormData({...formData, bio: e.target.value})} className="w-full p-5 bg-gray-50 rounded-3xl outline-none font-bold text-sm h-24 shadow-inner" />
          </section>

          {/* ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿØÿ±ÿßÿ≥Ÿä */}
          <section className="space-y-4">
            <h3 className="text-lg font-black text-[#1A365D] border-r-4 border-[#1A365D] pr-3">{t.eduTitle}</h3>
            {formData.educationList.map((edu, i) => (
              <div key={i} className="p-6 bg-gray-50/50 rounded-[2.5rem] border border-gray-100 space-y-4">
                <input placeholder={t.degree} value={edu.degree} onChange={e => {
                  const newList = [...formData.educationList]; newList[i].degree = e.target.value; setFormData({...formData, educationList: newList});
                }} className="w-full p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                <div className="grid grid-cols-2 gap-3">
                  <input placeholder={t.uni} value={edu.university} onChange={e => {
                    const newList = [...formData.educationList]; newList[i].university = e.target.value; setFormData({...formData, educationList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                  <input placeholder={t.year} type="number" value={edu.year} onChange={e => {
                    const newList = [...formData.educationList]; newList[i].year = e.target.value; setFormData({...formData, educationList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                </div>
              </div>
            ))}
            <button type="button" onClick={handleAddEdu} className="text-blue-600 font-black text-xs px-4">{t.addEdu}</button>
          </section>

          {/* ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑŸÖŸáŸÜŸä */}
          <section className="space-y-4">
            <h3 className="text-lg font-black text-[#1A365D] border-r-4 border-[#1A365D] pr-3">{t.expTitle}</h3>
            {formData.experienceList.map((exp, i) => (
              <div key={i} className="p-6 bg-gray-50/50 rounded-[2.5rem] border border-gray-100 space-y-4">
                <div className="grid grid-cols-2 gap-3">
                  <input placeholder={t.jobTitle} value={exp.title} onChange={e => {
                    const newList = [...formData.experienceList]; newList[i].title = e.target.value; setFormData({...formData, experienceList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                  <input placeholder={t.company} value={exp.company} onChange={e => {
                    const newList = [...formData.experienceList]; newList[i].company = e.target.value; setFormData({...formData, experienceList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                </div>
                <div className="grid grid-cols-3 gap-2">
                  <input placeholder={t.city} value={exp.city} onChange={e => {
                    const newList = [...formData.experienceList]; newList[i].city = e.target.value; setFormData({...formData, experienceList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                  <input placeholder={t.years} type="number" value={exp.durationYears} onChange={e => {
                    const newList = [...formData.experienceList]; newList[i].durationYears = e.target.value; setFormData({...formData, experienceList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                  <input placeholder={t.months} type="number" value={exp.durationMonths} onChange={e => {
                    const newList = [...formData.experienceList]; newList[i].durationMonths = e.target.value; setFormData({...formData, experienceList: newList});
                  }} className="p-4 bg-white rounded-2xl outline-none font-bold text-sm shadow-sm" />
                </div>
                <textarea placeholder={t.tasks} value={exp.tasks} onChange={e => {
                  const newList = [...formData.experienceList]; newList[i].tasks = e.target.value; setFormData({...formData, experienceList: newList});
                }} className="w-full p-4 bg-white rounded-2xl outline-none font-bold text-xs h-20 shadow-sm" />
              </div>
            ))}
            <button type="button" onClick={handleAddExp} className="text-blue-600 font-black text-xs px-4">{t.addExp}</button>
          </section>

          {/* ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ */}
          <section className="space-y-3">
            <h3 className="text-lg font-black text-[#1A365D] border-r-4 border-[#1A365D] pr-3">{t.skills}</h3>
            <input value={formData.skills} onChange={e => setFormData({...formData, skills: e.target.value})} className="w-full p-5 bg-gray-50 rounded-3xl outline-none font-bold text-sm shadow-inner" placeholder="ŸÖÿ´ÿßŸÑ: React, ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿ¥ÿßÿ±Ÿäÿπ, ÿßŸÑŸÇŸäÿßÿØÿ©..." />
          </section>

          <div className="mt-8 bg-[#1A365D]/5 p-6 rounded-[2.5rem] border border-[#1A365D]/10 text-center">
            <label className="flex items-start gap-4 cursor-pointer">
              <input type="checkbox" checked={isAgreed} onChange={e => setIsAgree(e.target.checked)} className="mt-1 w-6 h-6 accent-[#1A365D] rounded-lg flex-shrink-0" />
              <span className="text-xs font-bold text-[#1A365D] leading-relaxed">{t.declaration}</span>
            </label>
          </div>

          <button type="submit" disabled={loading || parsing} className="w-full py-6 rounded-[3rem] font-black shadow-2xl active:scale-95 transition-all text-xl bg-[#1A365D] text-white">
            {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏...' : t.finish}
          </button>
        </form>
      </div>
    </div>
  );
};
