import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const translations = {
  ar: {
    title: "ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÖŸÜÿµÿ©",
    langSection: "ŸÑÿ∫ÿ© ÿßŸÑŸàÿßÿ¨Ÿáÿ©",
    desc: "ÿßÿÆÿ™ÿ± ŸÑÿ∫ÿ™ŸÉ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ŸÑÿ™ÿÆÿµŸäÿµ ÿ™ÿ¨ÿ±ÿ®ÿ© ŸÉÿßÿ±Ÿäÿ±ŸÉ ÿ®ÿßŸÑŸÉÿßŸÖŸÑ",
    logout: "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ ÿßŸÑÿ¢ŸÖŸÜ",
    back: "ÿßŸÑÿπŸàÿØÿ©",
    confirmTitle: "ÿ™ÿ£ŸÉŸäÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿ∫ÿ©",
    confirmMsg: "ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿ™Ÿä ÿ≥Ÿäÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ±Ÿáÿß ÿ≥ÿ™ÿ∑ÿ®ŸÇ ÿπŸÑŸâ ÿ¨ŸÖŸäÿπ Ÿàÿßÿ¨Ÿáÿßÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ. ŸäŸÖŸÉŸÜŸÉ ÿßŸÑÿπŸàÿØÿ© Ÿàÿ™ÿ∫ŸäŸäÿ±Ÿáÿß ŸÅŸä ÿ£Ÿä ŸàŸÇÿ™.",
    confirmBtn: "ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±",
    cancelBtn: "ÿ™ÿ±ÿßÿ¨ÿπ"
  },
  en: {
    title: "Platform Settings",
    langSection: "App Language",
    desc: "Choose your preferred language to customize your Careerak experience",
    logout: "Secure Logout",
    back: "Go Back",
    confirmTitle: "Confirm Language Change",
    confirmMsg: "The selected language will be applied to all app interfaces. You can change it back anytime.",
    confirmBtn: "Confirm Change",
    cancelBtn: "Cancel"
  },
  fr: {
    title: "Param√®tres",
    langSection: "Langue de l'application",
    desc: "Choisissez votre langue pr√©f√©r√©e pour personnaliser votre exp√©rience",
    logout: "Se d√©connecter",
    back: "Retour",
    confirmTitle: "Confirmer le changement",
    confirmMsg: "La langue s√©lectionn√©e sera appliqu√©e √† toutes les interfaces. Vous pouvez la changer √† tout moment.",
    confirmBtn: "Confirmer",
    cancelBtn: "Annuler"
  }
};

export const SettingsPage = () => {
  const { changeLanguage, logout, language } = useAuth();
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);
  const [pendingLang, setPendingLang] = useState(null);

  useEffect(() => { setIsVisible(true); }, []);

  const t = translations[language] || translations.en;

  const languages = [
    { id: 'ar', label: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' },
    { id: 'en', label: 'English', flag: 'üá∫üá∏' },
    { id: 'fr', label: 'Fran√ßais', flag: 'üá´üá∑' }
  ];

  const handleLangClick = (langId) => {
    if (langId === language) return;
    setPendingLang(langId);
    setShowConfirm(true);
  };

  const confirmLangChange = () => {
    changeLanguage(pendingLang);
    setShowConfirm(false);
  };

  return (
    <div className={`min-h-screen pb-32 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} style={{ backgroundColor: '#E3DAD0' }}>
      
      {/* ŸÜÿßŸÅÿ∞ÿ© ÿßŸÑÿ™ÿ£ŸÉŸäÿØ ÿßŸÑŸÅÿÆŸÖÿ© */}
      {showConfirm && (
        <div className="fixed inset-0 z-[12000] flex items-center justify-center p-6 bg-[#1A365D]/40 backdrop-blur-md animate-fade-in">
          <div className="bg-white w-full max-w-md rounded-[3.5rem] p-10 shadow-2xl border-t-8 border-[#1A365D]">
             <div className="text-4xl mb-6 text-center">üåç</div>
             <h3 className="text-2xl font-black text-[#1A365D] mb-4 text-center">{t.confirmTitle}</h3>
             <p className="text-[#1A365D]/60 font-bold text-center leading-relaxed mb-10 px-2">{t.confirmMsg}</p>
             <div className="space-y-3">
                <button onClick={confirmLangChange} className="w-full py-5 bg-[#1A365D] text-white rounded-[2rem] font-black text-xl shadow-xl hover:bg-[#152c4d] active:scale-95 transition-all">{t.confirmBtn}</button>
                <button onClick={() => setShowConfirm(false)} className="w-full py-4 text-[#1A365D]/40 font-black text-sm hover:text-[#1A365D] transition-colors">{t.cancelBtn}</button>
             </div>
          </div>
        </div>
      )}

      <div className="px-6 py-10 max-w-3xl mx-auto">
        <div className="flex items-center justify-between mb-12">
          <h1 className="text-4xl font-black text-[#1A365D] tracking-tight">{t.title}</h1>
          <button onClick={() => navigate(-1)} className="px-6 py-3 bg-white/50 backdrop-blur-sm rounded-full border border-white text-xs font-black text-[#1A365D] uppercase tracking-widest active:scale-95 transition-all">
            {t.back}
          </button>
        </div>

        {/* ŸÇÿ≥ŸÖ ÿßŸÑŸÑÿ∫ÿ© */}
        <div className="bg-white rounded-[4rem] p-10 md:p-14 shadow-[0_40px_80px_-20px_rgba(26,54,93,0.1)] border border-white mb-10 transform transition-all">
          <div className="flex items-center gap-6 mb-10">
            <div className="w-16 h-16 bg-[#1A365D]/5 rounded-[1.5rem] flex items-center justify-center text-3xl shadow-inner">üåê</div>
            <div>
              <h2 className="text-2xl font-black text-[#1A365D]">{t.langSection}</h2>
              <p className="text-sm text-[#1A365D]/40 font-bold italic">{t.desc}</p>
            </div>
          </div>

          <div className="space-y-4">
            {languages.map((l) => (
              <button
                key={l.id}
                onClick={() => handleLangClick(l.id)}
                className={`w-full p-6 rounded-[2.5rem] flex items-center justify-between transition-all duration-500 group ${
                  language === l.id 
                  ? 'bg-[#1A365D] text-white shadow-2xl scale-[1.02]' 
                  : 'bg-gray-50 text-[#1A365D]/60 hover:bg-white border-2 border-transparent hover:border-[#1A365D]/5'
                }`}
              >
                <div className="flex items-center gap-5">
                  <span className="text-3xl filter group-hover:scale-110 transition-transform">{l.flag}</span>
                  <span className="font-black text-lg">{l.label}</span>
                </div>
                {language === l.id && (
                  <div className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center">
                    <span className="text-white text-xl">‚úì</span>
                  </div>
                )}
              </button>
            ))}
          </div>
        </div>

        {/* ÿ≤ÿ± ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ */}
        <button 
          onClick={logout}
          className="w-full p-8 bg-red-50 text-red-600 rounded-[3rem] font-black text-xl flex items-center justify-center gap-4 hover:bg-red-100 active:scale-95 transition-all shadow-sm border border-red-100"
        >
          <span className="text-2xl">üö™</span>
          {t.logout}
        </button>

        <p className="mt-16 text-center text-[#1A365D]/20 font-black text-[10px] uppercase tracking-[0.5em]">Careerak Version 1.0.0 &copy; 2024</p>
      </div>
    </div>
  );
};
