import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera } from '@capacitor/camera';
import { Geolocation } from '@capacitor/geolocation';

const translations = {
  ar: {
    welcome: "مرحباً بك في كاريرك",
    slogan: "شريكك المهني الذكي",
    description: "شريكك الاستراتيجي للارتقاء بمسارك المهني وإدارة مواردك البشرية بمنتهى الذكاء",
    start: "ابدأ الرحلة",
    jobs: "وظيفة",
    courses: "دورة",
    users: "مستخدم"
  },
  en: {
    welcome: "Welcome to Careerak",
    slogan: "Your Smart Career Partner",
    description: "Your strategic partner to advance your career and manage your HR with absolute intelligence",
    start: "Start Journey",
    jobs: "Jobs",
    courses: "Courses",
    users: "Users"
  },
  fr: {
    welcome: "Bienvenue sur Careerak",
    slogan: "Votre partenaire carrière intelligent",
    description: "Votre partenaire stratégique pour faire progresser votre carrière et gérer vos RH avec une intelligence absolue",
    start: "Commencer",
    jobs: "Emplois",
    courses: "Cours",
    users: "Utilisateurs"
  }
};

export const WelcomePage = ({ lang = 'ar' }) => {
  const navigate = useNavigate();
  const t = translations[lang] || translations.en;
  const isArabic = lang === 'ar';

  useEffect(() => {
    const requestPermissions = async () => {
      try {
        await Camera.requestPermissions();
        await Geolocation.requestPermissions();
      } catch (error) {}
    };
    requestPermissions();
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 text-center animate-fade-in" style={{ backgroundColor: '#E3DAD0' }}>
      <div className="mb-12 relative group">
        <div className="absolute inset-0 bg-[#1A365D] opacity-10 blur-[100px]"></div>
        <img 
          src="/logo.jpg" 
          alt="Careerak Logo" 
          className="relative h-48 w-48 mx-auto rounded-full shadow-[0_30px_60px_rgba(26,54,93,0.3)] border-[6px] border-white object-cover transform transition-all duration-700 hover:scale-105"
        />
      </div>

      <div className="max-w-2xl px-4 mb-16">
        <h1 className="text-5xl md:text-7xl font-black text-[#1A365D] mb-6 tracking-tight leading-none">
          {t.welcome}
        </h1>
        <div className="flex items-center justify-center gap-4 mb-8">
          <div className="h-[1px] w-12 bg-[#1A365D]/30"></div>
          <p className="text-xl md:text-2xl font-bold text-gray-600 italic">
            {t.slogan}
          </p>
          <div className="h-[1px] w-12 bg-[#1A365D]/30"></div>
        </div>
        <p className="text-gray-500 text-lg leading-relaxed max-w-md mx-auto">
          {t.description}
        </p>
      </div>

      <div className="w-full max-w-sm">
        <button
          onClick={() => navigate('/auth')}
          className="w-full bg-[#1A365D] hover:bg-[#2C5282] text-white font-black py-6 px-12 rounded-[2.5rem] shadow-[0_20px_40px_rgba(26,54,93,0.4)] transform transition-all active:scale-95 text-2xl group flex items-center justify-center gap-4"
        >
          <span>{t.start}</span>
          <span className={`transform transition-transform ${isArabic ? 'group-hover:-translate-x-2' : 'group-hover:translate-x-2'}`}>
            {isArabic ? '←' : '→'}
          </span>
        </button>
      </div>

      <div className="mt-20 grid grid-cols-3 gap-8 md:gap-16 opacity-30">
        <div className="flex flex-col items-center">
          <span className="text-2xl font-black text-[#1A365D]">10K+</span>
          <span className="text-[10px] font-bold uppercase tracking-widest">{t.jobs}</span>
        </div>
        <div className="flex flex-col items-center">
          <span className="text-2xl font-black text-[#1A365D]">500+</span>
          <span className="text-[10px] font-bold uppercase tracking-widest">{t.courses}</span>
        </div>
        <div className="flex flex-col items-center">
          <span className="text-2xl font-black text-[#1A365D]">50K+</span>
          <span className="text-[10px] font-bold uppercase tracking-widest">{t.users}</span>
        </div>
      </div>
    </div>
  );
};
