import React, { useState, useEffect } from 'react';
import { userService } from '../services/api';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { ImageCropper } from '../components/ImageCropper';

export const ProfilePage = ({ lang }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [saving, setSaving] = useState(false);
  const [showCropper, setShowCropper] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  
  // ‚úÖ ÿ≠ÿßŸÑÿßÿ™ ŸÖŸÜÿ¥ÿ¶ ÿßŸÑÿ≥Ÿäÿ±ÿ© ÿßŸÑÿ∞ÿßÿ™Ÿäÿ© (CV Generator)
  const [showCvOptions, setShowCvOptions] = useState(false);
  const [cvLang, setCvLang] = useState('ar');
  const [cvTemplate, setCvTemplate] = useState('modern');

  const isArabic = lang === 'ar';

  const t = {
    ar: {
      title: 'ŸÖŸÑŸÅŸä ÿßŸÑÿ¥ÿÆÿµŸä', edit: 'ÿ™ÿπÿØŸäŸÑ', cancel: 'ÿ•ŸÑÿ∫ÿßÿ°', save: 'ÿ≠ŸÅÿ∏',
      email: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä', phone: 'ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ', dept: 'ÿßŸÑŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿ™ÿπŸÑŸäŸÖŸä', pos: 'ÿßŸÑŸÖŸÜÿµÿ®',
      hrRole: 'üë®‚Äçüíº ŸÖÿ≥ÿ§ŸàŸÑ ÿ™Ÿàÿ∏ŸäŸÅ', empRole: 'üë§ ÿ®ÿßÿ≠ÿ´ ÿπŸÜ ÿπŸÖŸÑ',
      cvBtn: 'ÿ•ŸÜÿ¥ÿßÿ° ÿ≥Ÿäÿ±ÿ© ÿ∞ÿßÿ™Ÿäÿ© PDF ‚ú®',
      cvTitle: 'ÿ™ÿÆÿµŸäÿµ ÿ≥Ÿäÿ±ÿ™ŸÉ ÿßŸÑÿ∞ÿßÿ™Ÿäÿ©',
      cvLang: 'ŸÑÿ∫ÿ© ÿßŸÑÿ≥Ÿäÿ±ÿ© ÿßŸÑÿ∞ÿßÿ™Ÿäÿ©',
      cvTemplate: 'ÿßÿÆÿ™ÿ± ÿ¥ŸÉŸÑ ÿßŸÑÿ™ÿµŸÖŸäŸÖ',
      download: 'ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ≥Ÿäÿ±ÿ© ÿßŸÑÿ∞ÿßÿ™Ÿäÿ© ÿßŸÑÿ¢ŸÜ',
      templates: { modern: 'ÿπÿµÿ±Ÿä (Modern)', classic: 'ŸÉŸÑÿßÿ≥ŸäŸÉŸä (Classic)', minimal: 'ÿ®ÿ≥Ÿäÿ∑ (Minimal)' }
    },
    en: {
      title: 'My Profile', edit: 'Edit', cancel: 'Cancel', save: 'Save',
      email: 'Email', phone: 'Phone', dept: 'Education', pos: 'Position',
      hrRole: 'üë®‚Äçüíº HR Manager', empRole: 'üë§ Job Seeker',
      cvBtn: 'Generate CV PDF ‚ú®',
      cvTitle: 'Customize Your CV',
      cvLang: 'CV Language',
      cvTemplate: 'Select Template',
      download: 'Download CV Now',
      templates: { modern: 'Modern', classic: 'Classic', minimal: 'Minimal' }
    }
  }[lang];

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await userService.getProfile();
        setUser(response.data);
      } catch (error) { console.error('Error:', error); }
      finally { setLoading(false); }
    };
    fetchProfile();
  }, []);

  const handleImagePick = async () => {
    try {
      const image = await Camera.getPhoto({ quality: 90, allowEditing: false, resultType: CameraResultType.Uri, source: CameraSource.Prompt });
      setSelectedImage(image.webPath);
      setShowCropper(true);
    } catch (e) {}
  };

  const handleDownloadCV = () => {
    // ŸÖŸÜÿ∑ŸÇ ÿ™ŸàŸÑŸäÿØ ÿßŸÑŸÄ PDF ÿ≥Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿØÿπÿßÿ§Ÿá ŸáŸÜÿß
    alert(isArabic ? 'ÿ¨ÿßÿ±Ÿä ÿ™ÿ¨ŸáŸäÿ≤ ÿ≥Ÿäÿ±ÿ™ŸÉ ÿßŸÑÿ∞ÿßÿ™Ÿäÿ© ÿßŸÑÿ£ŸÜŸäŸÇÿ©...' : 'Preparing your elegant CV...');
    setShowCvOptions(false);
  };

  if (loading) return <div className="flex items-center justify-center min-h-screen bg-[#E3DAD0]"><div className="animate-spin rounded-full h-16 w-16 border-t-4 border-[#1A365D]"></div></div>;
  if (!user) return null;

  return (
    <div className="min-h-screen pb-24 px-6" style={{ backgroundColor: '#E3DAD0' }} dir={isArabic ? 'rtl' : 'ltr'}>
      {showCropper && <ImageCropper image={selectedImage} lang={lang} onCancel={() => setShowCropper(false)} onCropComplete={() => setShowCropper(false)} />}

      {/* ‚úÖ ŸÜÿßŸÅÿ∞ÿ© ÿÆŸäÿßÿ±ÿßÿ™ ÿßŸÑÿ≥Ÿäÿ±ÿ© ÿßŸÑÿ∞ÿßÿ™Ÿäÿ© PDF */}
      {showCvOptions && (
        <div className="fixed inset-0 z-[2000] flex items-center justify-center p-6 bg-black/60 backdrop-blur-md">
          <div className="bg-white w-full max-w-md rounded-[3rem] p-8 shadow-2xl animate-fade-in border-b-8 border-[#1A365D]">
            <h3 className="text-2xl font-black text-[#1A365D] mb-6 text-center">{t.cvTitle}</h3>
            
            <div className="space-y-6">
              <div>
                <label className="text-[10px] font-black uppercase text-gray-400 block mb-2">{t.cvLang}</label>
                <div className="flex gap-2">
                  <button onClick={() => setCvLang('ar')} className={`flex-1 py-3 rounded-xl font-bold border-2 transition-all ${cvLang === 'ar' ? 'bg-[#1A365D] text-white border-[#1A365D]' : 'bg-gray-50 border-transparent text-gray-400'}`}>ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</button>
                  <button onClick={() => setCvLang('en')} className={`flex-1 py-3 rounded-xl font-bold border-2 transition-all ${cvLang === 'en' ? 'bg-[#1A365D] text-white border-[#1A365D]' : 'bg-gray-50 border-transparent text-gray-400'}`}>English</button>
                </div>
              </div>

              <div>
                <label className="text-[10px] font-black uppercase text-gray-400 block mb-2">{t.cvTemplate}</label>
                <div className="grid grid-cols-1 gap-2">
                  {Object.entries(t.templates).map(([key, val]) => (
                    <button key={key} onClick={() => setCvTemplate(key)} className={`p-4 rounded-2xl text-right font-bold border-2 transition-all ${cvTemplate === key ? 'border-[#1A365D] bg-blue-50 text-[#1A365D]' : 'border-gray-50 bg-gray-50 text-gray-400'}`}>
                      {cvTemplate === key && 'üíé '} {val}
                    </button>
                  ))}
                </div>
              </div>

              <button onClick={handleDownloadCV} className="w-full py-5 bg-[#1A365D] text-white rounded-[2rem] font-black shadow-xl active:scale-95 transition-all mt-4">{t.download}</button>
              <button onClick={() => setShowCvOptions(false)} className="w-full text-gray-400 font-bold text-sm">ÿ•ÿ∫ŸÑÿßŸÇ</button>
            </div>
          </div>
        </div>
      )}

      <div className="max-w-3xl mx-auto">
        <div className="bg-[#1A365D] h-40 rounded-b-[4rem] shadow-2xl relative mb-16">
          <div className="absolute -bottom-12 left-1/2 -translate-x-1/2">
            <div className="relative">
              <div className="h-32 w-32 rounded-full border-8 border-white shadow-2xl overflow-hidden bg-gray-100">
                {user.profileImage ? <img src={user.profileImage} className="h-full w-full object-cover" /> : <span className="text-5xl flex items-center justify-center h-full">üë§</span>}
              </div>
              <button onClick={handleImagePick} className="absolute bottom-0 right-0 bg-white p-2 rounded-full shadow-lg border border-gray-100">üì∏</button>
            </div>
          </div>
        </div>

        <div className="text-center mb-10">
          <h1 className="text-3xl font-black text-[#1A365D] mb-2">{user.firstName} {user.lastName}</h1>
          <p className="text-xs font-bold text-gray-400 uppercase tracking-widest">{user.role === 'HR' ? t.hrRole : t.empRole}</p>
        </div>

        <div className="space-y-4">
          {/* ‚úÖ ÿ≤ÿ± ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ≥Ÿäÿ±ÿ© ÿßŸÑÿ∞ÿßÿ™Ÿäÿ© ŸÑŸÑÿ£ŸÅÿ±ÿßÿØ ŸÅŸÇÿ∑ */}
          {user.role !== 'HR' && (
            <button 
              onClick={() => setShowCvOptions(true)}
              className="w-full py-6 bg-white border-2 border-[#1A365D]/10 rounded-[2.5rem] shadow-xl text-[#1A365D] font-black text-lg flex items-center justify-center gap-3 hover:bg-gray-50 active:scale-95 transition-all"
            >
              üìÑ {t.cvBtn}
            </button>
          )}

          <div className="bg-white p-8 rounded-[3rem] shadow-2xl border border-white space-y-6">
            <div className="flex justify-between items-center border-b border-gray-50 pb-4">
              <h2 className="font-black text-[#1A365D]">{t.title}</h2>
              <button onClick={() => setEditing(!editing)} className="text-xs font-black text-blue-600 uppercase">{editing ? t.cancel : t.edit}</button>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {[ {l: t.email, v: user.email}, {l: t.phone, v: user.phone}, {l: t.dept, v: user.education}, {l: t.pos, v: user.position} ].map((item, i) => (
                <div key={i} className="space-y-1">
                  <label className="text-[9px] font-black text-gray-400 uppercase px-2">{item.l}</label>
                  <div className="bg-gray-50 p-4 rounded-2xl font-bold text-gray-700 text-sm">{item.v || '---'}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
