import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { userService } from '../services/api';
import { Preferences } from '@capacitor/preferences';
import { App } from '@capacitor/app';
import { useAuth } from '../context/AuthContext';
import CryptoJS from 'crypto-js';

const SECRET_KEY = 'careerak_secure_key_2024';

const loginTranslations = {
  ar: {
    title: "Careerak",
    subtitle: "ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„",
    userPlaceholder: "Ø§Ù„Ø¨Ø±ÙŠØ¯ Ø§Ù„Ø¥Ù„ÙƒØªØ±ÙˆÙ†ÙŠ Ø£Ùˆ Ø§Ø³Ù… Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…",
    passPlaceholder: "ÙƒÙ„Ù…Ø© Ø§Ù„Ù…Ø±ÙˆØ±",
    loginBtn: "Ø¯Ø®ÙˆÙ„",
    noAccount: "Ù„ÙŠØ³ Ù„Ø¯ÙŠÙƒ Ø­Ø³Ø§Ø¨ØŸ",
    createAccount: "Ø£Ù†Ø´Ø¦ Ø­Ø³Ø§Ø¨Ø§Ù‹ Ø§Ù„Ø¢Ù†",
    whatsappSupport: "WhatsApp Support"
  },
  en: {
    title: "Careerak",
    subtitle: "Login",
    userPlaceholder: "Email or Username",
    passPlaceholder: "Password",
    loginBtn: "Login",
    noAccount: "Don't have an account?",
    createAccount: "Create account now",
    whatsappSupport: "WhatsApp Support"
  },
  fr: {
    title: "Careerak",
    subtitle: "Connexion",
    userPlaceholder: "Email ou nom d'utilisateur",
    passPlaceholder: "Mot de passe",
    loginBtn: "Entrer",
    noAccount: "Pas de compte ?",
    createAccount: "CrÃ©er un compte",
    whatsappSupport: "WhatsApp Support"
  }
};

export default function LoginPage() {
  const navigate = useNavigate();
  const { language, audioEnabled } = useAuth();
  const t = loginTranslations[language] || loginTranslations.ar;
  const isRTL = language === 'ar';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isVisible, setIsVisible] = useState(false);
  
  const audioRef = useRef(null);

  useEffect(() => {
    setIsVisible(true);
    
    // Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„Ù…ÙˆØ³ÙŠÙ‚Ù‰ Ø§Ù„Ù…Ø³ØªÙ…Ø±Ø©
    if (audioEnabled) {
      if (!audioRef.current) {
        audioRef.current = new Audio('/Music.mp3');
        audioRef.current.loop = true;
        audioRef.current.volume = 0.5;
      }
      audioRef.current.play().catch(() => {});

      const handleAppState = (state) => {
        if (!audioRef.current) return;
        state.isActive ? audioRef.current.play().catch(() => {}) : audioRef.current.pause();
      };

      const listener = App.addListener('appStateChange', handleAppState);
      return () => {
        listener.then(l => l.remove());
        if (audioRef.current) audioRef.current.pause();
      };
    }
  }, [audioEnabled]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const response = await userService.login({ email, password });
      const encryptedToken = CryptoJS.AES.encrypt(response.data.token, SECRET_KEY).toString();
      await Preferences.set({ key: 'auth_token', value: encryptedToken });
      localStorage.setItem('user', JSON.stringify(response.data.user));
      
      const role = response.data.user.role?.toLowerCase();
      if (role === 'admin') navigate('/admin-dashboard');
      else if (role === 'hr') navigate('/hr-dashboard');
      else navigate('/profile');
    } catch (err) {
      setError(t.error || "Login Failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen flex items-center justify-center bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={isRTL ? 'rtl' : 'ltr'}>
      <div className="w-full max-w-sm bg-white rounded-[3rem] shadow-2xl p-10 mx-4 relative overflow-hidden">
        
        {/* Ø§Ù„Ù„ÙˆØ¬Ùˆ Ø§Ù„Ø£ØµÙ„ÙŠ */}
        <div className="flex flex-col items-center mb-8">
          <div className="w-24 h-24 rounded-full border-4 border-[#1A365D] p-1 mb-4 shadow-lg overflow-hidden">
             <img src="/logo.jpg" alt="Logo" className="w-full h-full object-cover rounded-full" />
          </div>
          <h1 className="text-3xl font-black text-[#1A365D] italic">Careerak</h1>
          <p className="text-gray-400 font-bold text-sm mt-1">{t.subtitle}</p>
        </div>

        {error && <div className="bg-red-50 text-red-600 p-3 rounded-xl text-xs font-bold mb-4 text-center border border-red-100">{error}</div>}

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder={t.userPlaceholder}
            className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-bold text-center"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder={t.passPlaceholder}
            className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-bold text-center"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-[#1A365D] text-white p-5 rounded-2xl font-black text-xl shadow-xl active:scale-95 transition-all mt-4"
          >
            {loading ? "..." : t.loginBtn}
          </button>
        </form>

        <div className="mt-8 text-center space-y-3">
          <p className="text-xs font-bold text-gray-400">
            {t.noAccount} <span onClick={() => navigate('/auth')} className="text-[#1A365D] cursor-pointer hover:underline">{t.createAccount}</span>
          </p>
          <button 
            onClick={() => window.open('https://wa.me/00201228195728', '_blank')}
            className="flex items-center justify-center gap-2 w-full text-[#25D366] font-bold text-xs"
          >
            {t.whatsappSupport} <span className="text-lg">ðŸ’¬</span>
          </button>
        </div>
      </div>
    </div>
  );
}
